package Class7;

public class DoWhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=1;
		
		while(num<=3) {
			System.out.println("GO BIRDS!!");
			num++;
		}
		System.out.println("-----------------------------------");
		
		int num1=1;
		
		do {
			System.out.println("GO BIRDS!!");
			num1++;
		}while(num1<=3);
	}

}
