package Class7;

public class Task1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// print all numbers from 50 to 20
		
		int a=50;
		
		while (a>=20) {
			System.out.println(a +" ");
			a--;
		}
		System.out.println("----------------------");
		
		// print all even numbers from 1 to 20
		
		int c = 2;
		
		while (c<=20) {
			System.out.println(c + " ");
			c+=2;
		}

	}

}
