package Homework;

public class TwentytoOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=20;
		
		while(num>=1) {
			if(num%2==0) {
				System.out.println(num); 
				
				
			}num--;
		}
		int num1=20;
		
		while(num1>=1) {
			System.out.println(num1);
			num1-=2;
		}
	}

}
